package com.cannontech.amr.moveInMoveOut.service;


import com.cannontech.amr.moveInMoveOut.bean.MoveInFormObj;
import com.cannontech.amr.moveInMoveOut.bean.MoveInResultObj;
import com.cannontech.amr.moveInMoveOut.bean.MoveOutFormObj;
import com.cannontech.amr.moveInMoveOut.bean.MoveOutResultObj;
import com.cannontech.database.data.lite.LiteYukonUser;

public interface MoveInMoveOutEmailService{

    /**
     * These functions take care of all the successful emails 
     * generated by the moveIn/moveOut Service
     * 
     * @param moveInResultObj
     * @param liteYukonUser
     */
    public void createMoveInSuccessEmail(MoveInResultObj moveInResultObj, LiteYukonUser liteYukonUser);
    public void createMoveOutSuccessEmail(MoveOutResultObj moveOutResultObj, LiteYukonUser liteYukonUser);
    
    /**
     * These functions take care of all the scheduled emails
     * generated by the moveIn/moveOut Service
     * 
     * @param moveInResultObj
     * @param liteYukonUser
     */
    public void createMoveInScheduleEmail(MoveInFormObj moveInFormObj, LiteYukonUser liteYukonUser);
    public void createMoveOutScheduleEmail(MoveOutFormObj moveOutFormObj, LiteYukonUser liteYukonUser);
    
    /**
     * These functions take care of all the failed emails 
     * generated by the moveIn/moveOut Service
     * 
     * @param moveInResultObj
     */
    public void createMoveInFailureEmail(MoveInResultObj moveInResultObj, LiteYukonUser liteYukonUser);
    public void createMoveOutFailureEmail(MoveOutResultObj moveOutResultObj, LiteYukonUser liteYukonUser);
}
